require 'sqlite3'

class Seeder
  def self.seed!
    drop_tables
    create_tables
    populate_tables
  end

  def self.drop_tables
    db.execute('DROP TABLE IF EXISTS leaders')
    db.execute('DROP TABLE IF EXISTS users')

  end

  def self.create_tables
    db.execute('CREATE TABLE leaders (
      id INTEGER PRIMARY KEY AUTOINCREMENT,
      country TEXT NOT NULL,
      name TEXT NOT NULL,
      continent TEXT NOT NULL,
      img TEXT NOT NULL)')

    puts "create table users"
    
    db.execute('CREATE TABLE users (
      id INTEGER PRIMARY KEY AUTOINCREMENT,
      user TEXT NOT NULL,
      password TEXT NOT NULL,
      score INTEGER

    )')

  end

  def self.populate_tables
    db.execute('INSERT INTO leaders (country, name, continent, img) VALUES ("USA", "Donald Trump", "north america", "data:image/webp;base64,")')
    db.execute('INSERT INTO users (user, password, score) VALUES ("philip", "123", 0)')
  end

  private
  def self.db
    return @db if @db
    @db = SQLite3::Database.new('db/WEB.sqlite')
    @db.results_as_hash = true
    @db
  end
end


Seeder.seed!